import { createContext,  useContext } from "react"
import { v4 } from "uuid";
import { useEffect, useState } from "react";
export const PlaygroundContext = createContext();

const initialData = [
    {
        id:v4(),
        title:"DSA",
        
        files:[
            {
                id:v4,
                title:'index',
                language:'cpp',
                code:'cout<<"Hello World";'
            }
        ]
    },
    {
        id:v4(),
        title:"Frontend",
        
        files:[
            {
                id:v4,
                title:'test',
                language:'javascript',
                code:`console.log("Hello World");`
            }
        ]
    }
]
export const defaultCodes = {
    'cpp' : `
        #include <iostream>
        int main()
        {
            std::cout<<"Hello World";

            return 0;
        }
    `,
    'javascript' : `console.log("Hello Javascript");`,
    'java' : `
        public class Main
        {
            public static void main(String[] args) {
                System.out.println("Hello World");
            }
        }
    `,
    'python' : `print("Hello Python")`,

}
export const PlaygroundProvider = ({children}) => {
    const [folders,setFolders] = useState(() => {
        const localData = localStorage.getItem('data');
        if(localData){
            return JSON.parse(localData);
        }
        localStorage.removeItem('data');
        return initialData;
    });
    const createNewPlayground = (newPlayground) => {
        const {fileName, folderName, language} = newPlayground;    
        const newFolders = [...folders];
        newFolders.push({
            id:v4(),
            title:folderName,
            files:[
                {
                    id:v4(),
                    title:fileName,
                    code:defaultCodes[language],
                    language
                }
            ]
        })
        localStorage.setItem('data',JSON.stringify(newFolders));
        setFolders(newFolders);
    }
    const createNewFolder = (folderName) => {
        const newFolder = {
            id:v4(),
            title:folderName,
            files:[]
        }
        
        const allFolders = [...folders, newFolder];
        localStorage.setItem('data',JSON.stringify(allFolders));
        setFolders(allFolders);
        
    }

    const deleteFolder = (id) => {
        //to delete the folder with given id
        //update the local storage
        const updatedFoldersList = folders.filter(folderItem => folderItem.id !== id);
        localStorage.setItem('data',JSON.stringify(updatedFoldersList));
        setFolders(updatedFoldersList);
    
    }
    const editFolderTitle = (newFolderName, id) => {
        const updatedFoldersList = folders.map(folderItem => {
            if(folderItem.id === id){
                folderItem.title = newFolderName;
            }
            return folderItem;
        })
        localStorage.setItem('data',JSON.stringify(updatedFoldersList));
        setFolders(updatedFoldersList);
    }
    const editFileTitle = (newFileName, folderId, fileId) => {
        const copiedFolders = [...folders];
        for(let i = 0;i < copiedFolders.length;i++){
            if(copiedFolders[i].id === folderId){
                const files = copiedFolders[i].files;
                for(let j = 0;j < files.length;j++){
                    if(files[j].id === fileId){
                        files[j].title = newFileName;
                        break;
                    }
                }
                break;
            }
        }

        localStorage.setItem('data',JSON.stringify(copiedFolders));
        setFolders(copiedFolders);
    }
    const deleteFile = (folderId,fileId) => {
        const copiedFolders = [...folders]
        for(let i = 0;i < copiedFolders.length;i++){
            if(copiedFolders[i].id === folderId){
                const files = [...copiedFolders[i].files];
                copiedFolders[i].files = files.filter((file) => {
                    return file.id !== fileId;
                })
                break;
            }
        }
        localStorage.setItem('data',JSON.stringify(copiedFolders))
        setFolders(copiedFolders)
    }
    const createPlayground = (folderId,file) => {
        const copiedFolders = [...folders]
        for(let i = 0;i < copiedFolders.length;i++){
           if(copiedFolders[i].id === folderId){
             copiedFolders[i].files.push(file)
             break;
           }
        }
        localStorage.setItem('data',JSON.stringify(copiedFolders))
        setFolders(folders)
    }

    const getDefaultCode = (fileId,folderId) => {
        for(let i = 0;i < folders.length;i++){
            
        }
    }

    useEffect(() => {
        if(!localStorage.getItem('data')){
            localStorage.setItem('data',JSON.stringify(folders));
        }
    },[])
    const playgroundFeatures = {
        folders,
        createNewPlayground,
        createNewFolder,
        deleteFolder,
        editFolderTitle,
        editFileTitle,
        deleteFile,
        createPlayground,
        getDefaultCode,
    }
    return(  
        <PlaygroundContext.Provider value={playgroundFeatures}>
            {children}
        </PlaygroundContext.Provider>
    )
}
