import "./EditorContainer.scss"
import Editor from "@monaco-editor/react"
import { useState,useRef } from "react"

const editorOptions = {
    fontSize:18,
    wordWrap:'on'
}

export const EditorContainer = () => {

    const [code,setCode] = useState('');
    const [language,setLanguage] = useState("cpp");
    const [theme,setTheme] = useState('vs-dark');
    const codeRef = useRef();



    const onChangeCode = (newCode) => {
        codeRef.current = newCode
    }
    
    const importCode = (event) => {
        const file = event.target.files[0];
        const fileType = file.type.includes("text")
        if(fileType){
            const fileReader = new FileReader();
            fileReader.readAsText(file);
            fileReader.onload = function(value){
                const importedCode = value.target.result;
                setCode(importedCode)
            }
        }
        else{
            alert("Please choose a program file!")
        }
    }

    const exportCode = () => {
        const codeValue = codeRef.current?.trim();
        if(!codeValue){
            alert("Please type some code in the editor before exporting")
        }

        //1.create a blob / instant file in the memory

        const codeBlob = new Blob([codeValue],{type:"text/plain"})
        
        //2.create the downloadable link with blob

        const downloadUrl = URL.createObjectURL(codeBlob);

        //3.create a clickable link to download the blob/file 
        const link = document.createElement("a");
        link.href = downloadUrl;
        //link.download = `code.${fileExtension}`
    }

    const onChangeLanguage = (e) => {
        setLanguage(newLanguage)
    }

    const onChangeTheme = (e) => {
        setTheme(newTheme)
    }


    return (
        <div className="root-editor-container">
            <div className="editor-header">
                <div className="editor-left-container">
                    <b className="title">{"Title of the card"}</b>
                    <span className="material-icons">edit</span>
                    <button>Save Code</button>
                </div>
                <div className="editor-right-container">
                    <select onChange={onChangeLanguage}>
                        <option value="cpp">CPP</option>
                        <option value="javascript">Javascript</option>
                        <option value="java">Java</option>
                        <option value="python">Python</option>
                    </select>
                    <select onChange={onChangeTheme}>
                        <option value="vs-dark">vs-dark</option>
                        <option value="vs-light">vs-light</option>
                    </select>
                </div>
            </div>
            <div className="editor-body">
                <Editor height={"100%"} language={"cpp"}
                options={editorOptions}
                theme={theme}
                onChange={onChangeCode}
                value={code}/>
            </div>
            <div className="editor-footer">
                <button className="btn">
                    <span className="material-icons">fullscreen</span>
                    <span>Full Screen</span>
                </button>
                <label htmlFor="import-code" className="btn">
                    <span className="material-icons">cloud_download</span>
                    <span>Import Code</span>
                </label>
                <input type="file" id="import-code" style={{display:'none'}} onChange={importCode}/>
                <button className="btn" onClick={exportCode}> 
                    <span className="material-icons">cloud_upload</span>
                    <span>Export Code</span>
                </button>
                <button className="btn">
                    <span className="material-icons">play_arrow</span>
                    <span>Run Code</span>
                </button>
            </div>
        </div>
    )
}
